@@ -0,0 +1,156 @@
+/* RESET + BASE */
+* {
+  margin: 0;
+  padding: 0;
+  box-sizing: border-box;
+  font-family: "Poppins", sans-serif;
+}
+
+/* VARIABLES */
+:root {
+  --bg: #f9f9f9;
+  --text: #111;
+  --card: #fff;
+  --muted: #777;
+  --accent: #ff3c7e;
+  --accent2: #ff993c;
+}
+
+body {
+  background: linear-gradient(135deg, #ffecd2, #fcb69f);
+  color: var(--text);
+  min-height: 100vh;
+  display: flex;
+  flex-direction: column;
+  transition: all 0.3s ease;
+}
+
+/* NAVBAR */
+.navbar {
+  display: flex;
+  justify-content: space-between;
+  align-items: center;
+  padding: 1rem 2rem;
+  background: rgba(255,255,255,0.9);
+  border-radius: 0 0 12px 12px;
+  box-shadow: 0 4px 15px rgba(0,0,0,0.1);
+}
+
+/* LINKS */
+.links a {
+  text-decoration: none;
+  margin: 0 0.8rem;
+  color: var(--text);
+  font-weight: 500;
+  position: relative;
+}
+.links a.active::after {
+  content: "";
+  position: absolute;
+  bottom: -4px;
+  left: 0;
+  right: 0;
+  height: 2px;
+  background: linear-gradient(45deg, var(--accent), var(--accent2));
+  border-radius: 2px;
+}
+
+/* MAIN */
+.container {
+  padding: 2rem 1rem;
+  max-width: 1200px;
+  margin: auto;
+}
+.feed-head {
+  text-align: center;
+  margin-bottom: 1.5rem;
+}
+.feed-head h2 {
+  font-size: 2rem;
+  margin-bottom: 0.5rem;
+  color: var(--accent);
+  text-shadow: 1px 1px 2px rgba(0,0,0,0.2);
+}
+.muted {
+  color: var(--muted);
+}
+
+/* GRID FEED */
+.feed-grid {
+  display: grid;
+  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
+  gap: 1.5rem;
+}
+
+/* CARD */
+.card {
+  background: var(--card);
+  border-radius: 20px;
+  overflow: hidden;
+  box-shadow: 0 10px 25px rgba(0,0,0,0.15);
+  transition: transform 0.3s, box-shadow 0.3s;
+}
+.card:hover {
+  transform: translateY(-6px);
+  box-shadow: 0 15px 30px rgba(0,0,0,0.25);
+}
+.card img, .card video {
+  width: 100%;
+  height: 380px;
+  object-fit: cover;
+  border-bottom: 1px solid #eee;
+}
+.card p {
+  padding: 1rem;
+  font-size: 0.95rem;
+  color: #333;
+}
+.card .actions button {
+  border: none;
+  background: linear-gradient(45deg, var(--accent), var(--accent2));
+  color: #fff;
+  padding: 0.5rem 1.2rem;
+  border-radius: 10px;
+  cursor: pointer;
+  transition: 0.3s;
+}
+.card .actions button:hover {
+  opacity: 0.9;
+  transform: scale(1.05);
+}
+
+/* FOOTER */
+.footer {
+  text-align: center;
+  padding: 2rem 1rem;
+  color: #fff;
+  background: rgba(0,0,0,0.6);
+  backdrop-filter: blur(8px);
+  border-top-left-radius: 20px;
+  border-top-right-radius: 20px;
+  box-shadow: 0 -5px 15px rgba(0,0,0,0.2);
+}
+.footer .social-links {
+  display: flex;
+  justify-content: center;
+  gap: 1rem;
+  margin-top: 0.5rem;
+}
+.footer .social-links a {
+  font-size: 1.3rem;
+  transition: transform 0.3s, color 0.3s;
+}
+.footer .social-links a:hover {
+  transform: scale(1.2);
+  color: var(--accent);
+}
+
+/* RESPONSIVE */
+@media (max-width: 768px) {
+  .feed-grid {
+    grid-template-columns: 1fr;
+  }
+  .card img, .card video {
+    height: 300px;
+  }
+}

