commits.content:
/* RESET + BASE */
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Poppins", sans-serif;
}

:root {
  --bg: #f9f9f9;
  --text: #111;
  --card: #fff;
  --muted: #777;
  --accent: #ff3c7e;
  --accent2: #ff993c;
}

body.dark {
  --bg: #121212;
  --text: #eee;
  --card: #1e1e1e;
  --muted: #aaa;
}

body {
  background: var(--bg);
  color: var(--text);
  transition: all 0.3s ease;
}

/* NAVBAR */
.navbar {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
  background: rgba(255,255,255,0.9);
  backdrop-filter: blur(10px);
  box-shadow: 0 4px 15px rgba(0,0,0,0.1);
  position: sticky;
  top: 0;
  z-index: 100;
  border-radius: 0 0 12px 12px;
}
body.dark .navbar {
  background: rgba(30,30,30,0.9);
}

.logo {
  font-weight: 700;
  font-size: 1.5rem;
  color: var(--accent);
}

.links a {
  text-decoration: none;
  margin: 0 0.8rem;
  color: var(--text);
  font-weight: 500;
  transition: 0.3s;
  position: relative;
}
.links a:hover {
  color: var(--accent);
}
.links a.active::after {
  content: "";
  position: absolute;
  bottom: -4px;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(45deg, var(--accent), var(--accent2));
  border-radius: 2px;
}

.actions button {
  border: none;
  background: none;
  cursor: pointer;
  font-size: 1.3rem;
  transition: 0.3s;
}
.actions button:hover {
  transform: scale(1.2);
}

/* CONTAINER */
.container {
  padding: 2rem 1rem;
  max-width: 1200px;
  margin: auto;
}

/* FEED HEAD */
.feed-head {
  text-align: center;
  margin-bottom: 2rem;
}
.feed-head h2 {
  font-size: 2rem;
  margin-bottom: 0.3rem;
}
.muted {
  color: var(--muted);
}

/* GRID FEED */
.feed-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 1.5rem;
}

/* POST CARD */
.card, .post-card {
  background: var(--card);
  border-radius: 16px;
  overflow: hidden;
  box-shadow: 0 8px 20px rgba(0,0,0,0.08);
  transition: transform 0.3s, box-shadow 0.3s;
}
.card:hover, .post-card:hover {
  transform: translateY(-5px);
  box-shadow: 0 12px 25px rgba(0,0,0,0.15);
}

.card img, .card video, .post-card img, .post-card video {
  width: 100%;
  height: 400px;
  object-fit: cover;
  display: block;
  border-bottom: 1px solid rgba(0,0,0,0.1);
  border-radius: 16px 16px 0 0;
}

.post-info {
  padding: 1rem 1.2rem;
}
.post-info h4 {
  font-weight: 600;
}
.post-info p {
  margin-top: 0.4rem;
  color: var(--muted);
}

/* POST ACTIONS */
.actions, .post-actions {
  display: flex;
  justify-content: space-between;
  margin-top: 1rem;
}
.actions button, .post-actions button {
  border: none;
  background: linear-gradient(45deg, var(--accent), var(--accent2));
  color: #fff;
  font-weight: 600;
  cursor: pointer;
  padding: 0.6rem 1.2rem;
  border-radius: 12px;
  transition: all 0.3s;
}
.actions button:hover, .post-actions button:hover {
  transform: scale(1.05);
  opacity: 0.9;
}

/* AUTH PAGE */
.auth-page {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background: linear-gradient(135deg, #ff9a9e, #fad0c4);
}

.auth-card {
  background: var(--card);
  padding: 3rem 2rem;
  border-radius: 20px;
  box-shadow: 0 10px 30px rgba(0,0,0,0.15);
  width: 100%;
  max-width: 400px;
  text-align: center;
  transition: 0.3s;
}
.auth-card h2 {
  margin-bottom: 1.5rem;
  color: var(--accent);
}
.auth-card input {
  width: 100%;
  padding: 12px 15px;
  margin: 10px 0;
  border-radius: 10px;
  border: 1px solid #ccc;
  outline: none;
  transition: 0.3s;
}
.auth-card input:focus {
  border-color: var(--accent);
  box-shadow: 0 0 8px rgba(255,60,126,0.4);
}
.auth-card button {
  width: 100%;
  padding: 12px;
  margin-top: 15px;
  background: linear-gradient(45deg, var(--accent), var(--accent2));
  border: none;
  color: white;
  font-size: 16px;
  font-weight: bold;
  border-radius: 10px;
  cursor: pointer;
  transition: 0.3s;
}
.auth-card button:hover {
  opacity: 0.9;
  transform: scale(1.02);
}
.auth-card p {
  margin-top: 15px;
  font-size: 14px;
  color: var(--muted);
}
.auth-card a {
  color: var(--accent);
  font-weight: bold;
  text-decoration: none;
}

/* FOOTER */
.footer {
  text-align: center;
  padding: 1.5rem;
  margin-top: 2rem;
  color: var(--muted);
  border-top: 1px solid rgba(0,0,0,0.1);
}

/* RESPONSIVE */
@media (max-width: 768px) {
  .navbar {
    flex-direction: column;
    gap: 0.5rem;
  }
  .feed-grid {
    grid-template-columns: 1fr;
  }
  .card img, .card video {
    height: 300px;
  }
}
